
# Load librarieslibrary(ggplot2)library(dplyr)# Sample data creationset.seed(123)age_data <- data.frame(  age = sample(15:60, 50, replace = TRUE),  gender = sample(c("Male", "Female"), 50, replace = TRUE))#Count the number of athletes in each age group by genderage_data_summary <- age_data %>%  group_by(age, gender) %>%  summarize(count = n()) %>%  mutate(count = ifelse(gender == "Female", -count, count))  # Negative counts for Female to separate sides# Plottingggplot(age_data_summary, aes(x = count, y = age, fill = gender)) +  geom_bar(stat = "identity", width = 0.8) +  scale_x_continuous(labels = abs) +  # Show positive labels for both sides  labs(x = "Number of Athletes", y = "Age", title = "Age and Gender Distribution of Athletes") +  scale_fill_manual(values = c("Male" = "blue", "Female" = "lightblue")) +  theme_minimal() +  theme(    axis.title.y = element_text(size = 12, face = "bold"),    axis.title.x = element_text(size = 12, face = "bold"),    plot.title = element_text(size = 14, face = "bold")  )